mapper.select:
select count(*) from exam where
exam_Title LIKE '%'|| '#{keyward}' ||'%' and
class_no = #{classNo}

select r.rnum, r.ordernum, r.problemtitle, r.result, r.type from(select rownum rnum, o.orderNum, o.problemTitle, o.result, o.type from(select q.order_num orderNum, p.problem_title problemTitle,
decode(p.answer,s.submit_answer,'O','X') result, p.type type
from solve s, question q, problem p where s.question_no = q.question_no and p.problem_no = q.problem_no and q.exam_no = ${examNo} and s.join_no = ${joinNo} order by q.order_num desc)o)r where r.rnum >= #{startNum} and r.rnum <= #{endNum}

select al.join_no joinNo, u.name username, decode(al.count,cl.count,'제출','진행중') result
from users u ,(select e.exam_no, j.user_no, j.join_no, count(*) count FROM question q , exam e, class c , join_users j where e.exam_no = q.exam_no and e.class_no = c.class_no and j.class_no = c.class_no group by j.user_no,e.exam_no,j.join_no) al , (SELECT e.exam_no, j.user_no, j.join_no, count (*) count FROM question q , exam e, class c , join_users j ,solve s where e.exam_no = q.exam_no and e.class_no = c.class_no and j.class_no = c.class_no and j.join_no = s.join_no and s.question_no = q.question_no and (s.status = '제출' or s.status = '완료')
group by j.user_no,e.exam_no,j.join_no)cl where al.exam_no = cl.exam_no(+) and al.user_no = cl.user_no(+) and u.no = al.user_no and al.exam_no = #{examNo}

select exam_no examNo, exam_title examTitle, exam_type examType, time,
TO_CHAR(start_date,'yyyy-MM-dd hh:mm:ss') startDate, TO_CHAR(end_date,'yyyy-MM-dd hh:mm:ss') endDate
from exam where exam_no = #{examNo}

mapper.update:

update exam

set 



		exam_title = #{examTitle}



		,
		exam_type = #{examType}


	,
    time = #{time}





    	,
		start_date = TO_DATE(#{startDate},'yyyy-mm-dd hh:mi:ss')



     	,
		end_date = TO_DATE(#{endDate},'yyyy-mm-dd hh:mi:ss')



where exam_no = #{examNo}
