mapper.select:
select r.rnum, r.no, r.name, r.title, r.hit, r.reg_date from (select rownum rnum, o.no, o.name, o.title, o.hit, o.reg_date from (select n.notice_no no, u.name, n.title, n.content, n.hit, to_char(n.reg_date, 'YYYY-MM-DD') as reg_date from users u, join_users j, notice n where n.join_no = j.join_no and j.user_no = u.no
and title like '%' || #{keyword} || '%'
order by no desc) o ) r where r.rnum >= #{startRNum} and r.rnum <= #{endRNum}

mapper.update:
	update notice
	set title = #{title},
   	    content = #{content}
    where notice_no = #{no}

update notice
set hit = hit+1
where notice_no = #{no}
